	,MDES=#{mdes,jdbcType=VARCHAR},MCOM=#{mcom,jdbcType=VARCHAR},MIMG=#{mimg,jdbcType=VARCHAR}
	,MUPDATEDATE=SYSDATE
WHERE MNUM=#{mnum,jdbcType=VARCHAR}
AND MDELETEYN='Y'
